def part1():
    etage = 0
    for x in l:
        if x == '(':
            etage += 1
        if x == ')':
            etage -= 1
    print('Part 1:',etage)

def part2():

    etage = 0
    for x,y in zip(l,range(1,len(l))):
        if x == '(':
            etage += 1
        if x == ')':
            etage -= 1
        if etage < 0:
            print('Part 2:',y)
            break
